class second{	public static void main(String[] argv){		prog p1 = new prog();		p1.runCode();	}}class prog{	void runCode(){		int tot=0;		int a=1;		int b=1;		int c=2;		int tmp=0;		while(c<4000000){			System.out.println("\nC er "+ c);			if(c%2==0){				tot+=c;				System.out.println("La til "+ c);			}			tmp=c;			a=b;			b=c;			c=a+b;					}		System.out.println("\n"+ tot);	}}